                userName: 'a@a.com',
                isAuthenticating: false,
                isAuthenticated: true,
                statusText: 'You have been successfully logged in.'
            }
        );
    });

    it('should handle AUTH_LOGIN_USER_FAILURE', () => {
        const reducerResponse = authReducer([],
            {
                type: TYPES.AUTH_LOGIN_USER_FAILURE,
